<template>
	<div class="footer" v-if="isShowMfooter">
		<div class="footer-in">
			<a v-for="(item, index) in tabs" :key="index" class="list" @click="toggleTab(index)">
				<i class="icon" :style="{ backgroundImage:now===index?'url(' + item.pictureUrl2 + ')':'url(' + item.pictureUrl1 + ')' }"></i>
				{{ item.name }}
			</a>
		</div>
	</div>
</template>

<script>
import Vue from 'vue';
export default Vue.extend({
	computed:{
	  isShowMfooter(){
	    // 从仓库获取值到组件
	    return this.$store.state.isShowMfooter
	  }
	},
	data() {
		return {
			now:0,
			tabs: [
				{
					// 选项卡的名字
					name: '首页',
					// 跳转路由
					url: 'home',
					pictureUrl1:'data:image/png;base64,',
					// 图标
					pictureUrl2:'data:image/png;base64,'
				},
				{
					name: '分类',
					url: 'category',
					pictureUrl1:'//img07.yiguoimg.com/d/web/180508/01315/170504/nav_classify.png',
					pictureUrl2:'//img07.yiguoimg.com/e/web/170110/00583/164146/nav_classify_active.png',
				},
				{
					name: '吃饭吧',
					url: '',
					pictureUrl1:
						'data:image/png;base64,',pictureUrl2:'data:image/png;base64,'
				},
				{
					name: '购物车',
					url: 'cart',
					pictureUrl1:'//img05.yiguoimg.com/e/web/170110/00583/164146/nav_list.png',
					pictureUrl2:'//img07.yiguoimg.com/e/web/170110/00583/164146/nav_list_active.png',
				},
				{
					name: '我的易果',
					url: 'me',
					pictureUrl1:'//img07.yiguoimg.com/e/web/170110/00583/164146/nav_user.png',
					pictureUrl2:'//img05.yiguoimg.com/e/web/170110/00583/164146/nav_user_active.png',
				}
			]
		};
	},
	methods:{
	  toggleTab(tab){
		  this.now=tab
	    // 编程式导航
	    this.$router.push({
	      name: this.tabs[tab].url
	    })
	    // console.log(this)
	  }
	},
	computed:{
	  isShowMfooter(){
	    // 从仓库获取值到组件
	    return this.$store.state.isShowMfooter
	  }
	}
});
</script>

